package com.jdk.basicprog;

public class Doubleforloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
         
		  
				for(int b=18; b>=1;b--) //Repetation->2
				 {
				       for(int a=1; a<=b;a++) //repetation->4
					     {
						   System.out.print("*"); //2*4 =>8 o/p
					     }
				           System.out.println();
				 }
	         }
          }
					    
				     
			      

			   
		         
         
        
