package com.jdk.basicprog;

public class forloops1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        for (int b=1;b<=8;b++)
        {
        	for(int a=1;a<=b;a++)
        	{
        		System.out.print("*");
        	}
        	System.out.println();
        }
	}

}
