package com.jdk.basicprog;

public class forloops2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int a=7; a>=1; a--) //line
        { 
            
            for (int b=1; b<=a; b++)//star print
            { 
                System.out.print(b);
            } 
  
            System.out.println();
            }
        }  
	}


