package com.jdk.basicprog;

public class forloops3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        for(int a=1;a<=10;a++) // line create 
        {
        	for(int c=10;c>a;c--) // space create
        	{
        		System.out.print(" ");
            }
        	for(int b=1;b<=a;b++) // star excute
        	{
        		System.out.print("*");
        	}
        	System.out.println();
     	}
	}  

}
