package com.jdk.basicprog1;

public class evenodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int n=6;
         
         if((n%2)==0)
         {
        	 System.out.println("Even No = " + n);
         }
         else 
         {
        	 System.out.println("Odd No = " + n);
         }
	}

}
