package package1;

public class Casting1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        //Implicite casting
		byte a=10;
		int b;
		
		b=a;
		System.out.println(a);
		System.out.println(b);
		
		//explicite casting
		short c=20;
		byte d;
		
		d=(byte)c;
		System.out.println(c);
		System.out.println(d);
		
		
	
	}

}
